import re

def get_count(input_str):
    num_vowels = len(re.findall('[aeiou]',input_str))    
    return num_vowels

def get_count2(input_str):
    return sum(c in 'aeiou' for c in input_str)

def test_short_string():
    assert get_count('abracadabra') == 5
    assert get_count2('abracadabra') == 5

def test_medium_string():
    assert get_count('o a kak ushakov lil vo kashu kakao') == 13
    assert get_count2('o a kak ushakov lil vo kashu kakao') == 13

